opts.nonPropulsionSystemMass = opts.parachuteMass + opts.electronicsMass + opts.bodyTubeMass + opts.payloadMass;

opts.engineDryMass = physicalDesignState.engineDryMass;
opts.propellantMass = physicalDesignState.propellantMass;
opts.oxidizerMass = physicalDesignState.oxidizerMass;
opts.fuelMass = physicalDesignState.fuelMass;

opts.dryMass = opts.nonPropulsionSystemMass + opts.engineDryMass;
opts.wetMass = opts.dryMass + opts.propellantMass;

opts.externalRadius = max(physicalDesignState.opts.ccRadius, physicalDesignState.opts.OxidizerTankRadius);